mapper.insert:

  SELECT LAST_INSERT_ID()

insert into busi_award_record (award_no, award_money, create_time, 
  res_id, res_type, user_id, 
  award_status, standby1, standby2
  )
values (#{awardNo,jdbcType=VARCHAR}, #{awardMoney,jdbcType=DECIMAL}, #{createTime,jdbcType=TIMESTAMP}, 
  #{resId,jdbcType=INTEGER}, #{resType,jdbcType=VARCHAR}, #{userId,jdbcType=INTEGER}, 
  #{awardStatus,jdbcType=VARCHAR}, #{standby1,jdbcType=VARCHAR}, #{standby2,jdbcType=VARCHAR}
  )

mapper.select:
SELECT t1.award_record_id awardRecordId, t1.award_money awardMoney, t1.create_time createTime, t1.res_id resId, t1.award_no awardNo, t1.award_status awardStatus, t1.res_type resType, t1.user_id userId, t2.nickname, t3.video_audio_title videoAudioTitle, t2.phone, t2.headimgurl FROM busi_award_record t1 LEFT JOIN busi_user t2 ON t1.user_id = t2.user_id LEFT JOIN busi_video_audio t3 ON t1.res_id = t3.video_audio_id
and t2.nickname like CONCAT('%',#{nickname},'%')
and t2.phone like CONCAT('%',#{phone},'%')
and t1.create_time >= #{startDate}
and t1.create_time <= #{endDate}
and t1.award_status = #{awardStatus}
ORDER BY t1.create_time DESC
