mapper.update:
update busi_resources_thumbs
set thumbs_status = 2
where thumbs_id = #{thumbsId,jdbcType=INTEGER}

mapper.select:
	select 

  from busi_resources_thumbs
  where res_type = "YL"

  	and res_id = #{resId}


  	and user_id = #{userId}

mapper.update:
update busi_resources_thumbs 
set thumbs_status = '2'
where res_id = #{resId}
and user_id = #{userId}
