mapper.select:
 select 

 from busi_say
 where 1 = 1

 	and say_id = #{sayId} 


 	   and DATE_FORMAT(create_time, '%Y-%m-%d')>=  DATE_FORMAT(#{beginDate}, '%Y-%m-%d')   


		  and DATE_FORMAT(create_time, '%Y-%m-%d') <= DATE_FORMAT(#{endDate}, '%Y-%m-%d')    


 	   and  say_eval_num >= #{evalMin}  


		  and  say_eval_num <= #{evalMax}   


 	   and  say_thumbs_num >= #{thumbsMin}  


		  and  say_thumbs_num <= #{thumbsMax}   

 and say_status = 1
 order by create_time desc

mapper.update:
    update busi_say



	    		say_eval_num = say_eval_num + #{num}


	    		say_thumbs_num = say_thumbs_num + #{num}



    where say_id = #{sayId}
