mapper.select:
select ,(select CONCAT(u.username,'/',u.phone) from busi_user u where u.user_id = o.classmate_user_id) as classmateName from busi_user o where user_status = 1
and( o.username like CONCAT('%', #{userName},'%') or o.nickname like CONCAT('%', #{userName},'%') or o.phone like CONCAT('%', #{userName},'%') )
and o.auth_status = #{authStatus}
and o.is_agency = #{isAgency}
and DATE_FORMAT(o.create_time, '%Y-%m-%d')>= DATE_FORMAT(#{beginDate}, '%Y-%m-%d')
and o.openid = #{openid}
and DATE_FORMAT(o.create_time, '%Y-%m-%d') <= DATE_FORMAT(#{endDate}, '%Y-%m-%d')
order by o.create_time desc

  select 

  from busi_user
  where user_status = 1

  and(
  	username like CONCAT('%', #{userName},'%')
		or nickname like CONCAT('%', #{userName},'%')
		or phone like CONCAT('%', #{userName},'%')
		)


		and auth_status = #{authStatus}


		and is_agency = #{isAgency}



		 and is_agency<>1 


  	   and DATE_FORMAT(create_time, '%Y-%m-%d')>=  DATE_FORMAT(#{beginDate}, '%Y-%m-%d')   


  	and openid = #{openid}


  	and user_id = #{userId}


  	and classmate_user_id = #{classmateUserId}


  	and classmate_user_id is null;

mapper.update:
  		update busi_user
    	set integral_num = integral_num + #{integral}
	    where user_id = #{userId}

mapper.select:
SELECT t1.order_id orderId, t1.pay_status payStatus, t1.order_pay orderPay, t1.order_person_num orderPersonNum, t1.create_time createTime, t2.course_id id, t2.course_title title, t2.course_price price, t2.course_intro intro, t2.course_speaker speaker, t2.course_duration duration, t2.introvideo_url introvideoUrl, t2.course_picture picture, t2.course_logo logo, t2.course_detail detail, t3.user_id userId, t3.nickname, t3.username, t3.is_agency isAgency, t3.phone FROM busi_order t1 LEFT JOIN busi_course t2 ON t1.res_id = t2.course_id LEFT JOIN busi_user t3 ON t1.user_id = t3.user_id LEFT JOIN busi_agent t4 ON t1.user_id = t4.user_id WHERE t1.res_type = 'KC' AND t1.pay_status = '2' AND t1.order_status = '1'
AND t4.agent_user_id = #{userId}
AND t4.class_emp_id = #{userId}
and( t3.username like CONCAT('%', #{userName},'%') or t3.phone like CONCAT('%', #{userName},'%') )
UNION ALL SELECT t1.order_id orderId, t1.pay_status payStatus, t1.order_pay orderPay, t1.order_person_num orderPersonNum, t1.create_time createTime, t2.video_audio_id id, t2.video_audio_title title, t2.video_audio_price price, t2.video_audio_intro intro, NULL speaker, NULL duration, NULL introvideoUrl, NULL picture, t2.video_audio_logo logo, NULL detail, t3.user_id userId, t3.nickname, t3.username, t3.is_agency isAgency, t3.phone FROM busi_order t1 LEFT JOIN busi_video_audio t2 ON t1.res_id = t2.video_audio_id LEFT JOIN busi_user t3 ON t1.user_id = t3.user_id LEFT JOIN busi_agent t4 ON t1.user_id = t4.user_id WHERE t1.res_type <> 'KC' AND t1.pay_status = '2' AND t1.order_status = '1'
AND t4.agent_user_id = #{userId}
AND t4.class_emp_id = #{userId}
and( t3.username like CONCAT('%', #{userName},'%') or t3.phone like CONCAT('%', #{userName},'%') )

SELECT t1.user_id userId, t1.openid, t1.nickname, t1.sex, t1.province, t1.city, t1.country, t1.headimgurl, t1.username, t1.integral_num integralNum, t1.auth_status authStatus, t1.user_status userStatus, t1.card_type cardType, t1.card_num cardNum, t1.create_time createTime, t1.classmate_user_id classmateUserId, t1.phone, t1.standby1, t1.standby2, t1.is_agency isAgency, t1.member_set_id memberSetId, t2.member_set_name memberSetName FROM busi_user t1 LEFT JOIN busi_member_set t2 ON t1.member_set_id = t2.member_set_id where t1.user_status = 1
and( t1.username like CONCAT('%', #{userName},'%') or t1.nickname like CONCAT('%', #{userName},'%') or t1.phone like CONCAT('%', #{userName},'%') )
and t1.auth_status = #{authStatus}
and t1.is_agency = #{isAgency}
and t2.member_set_name like CONCAT('%', #{memberSetName},'%')
ORDER BY t1.create_time DESC
