mapper.select:
    select 

    from busi_user
    where user_status = 1

    	and phone = #{phone}


  		and username like CONCAT('%', #{userName},'%')


  		and auth_status = #{authStatus}


    	   and DATE_FORMAT(create_time, '%Y-%m-%d')>=  DATE_FORMAT(#{beginDate}, '%Y-%m-%d')   


    	and openid = #{openid}


   		  and DATE_FORMAT(create_time, '%Y-%m-%d') <= DATE_FORMAT(#{endDate}, '%Y-%m-%d')    

	order by create_time desc

mapper.update:
  		update busi_user
    	set integral_num = integral_num + #{integral}
	    where user_id = #{userId}
